define('flashNotifier', ['jquery'], function($) {
  /**
   * Initialize the flash notifier.
   */
  window.flashNotifier = function(params) {
    window.flashNotifierParams = params;
    window.flashNotifierIndex = 0;
    window.flashNotifierNext();
  };

  /**
  * Display the next popup.
  */
  window.flashNotifierNext = function() {
    // If index > length, stop it!
    if(window.flashNotifierIndex >= window.flashNotifierParams.length) {
      return;
    }
    // Get the params.
    var params = window.flashNotifierParams[window.flashNotifierIndex];
    // When window has already been loaded.
    if(document.readyState === 'complete') {
      window.flashPopup.modal('show');
    } else {
      // Show the popup.
      $($.proxy(window.flashPopup, 'modal', 'show'));
    }
  };

  /**
   * Confirm that the user has seen the popups.
   */
  window.flashNotifierConfirm = function() {
    let params = window.flashNotifierParams[window.flashNotifierIndex];
    // Workaround for XWIKI-20623 : JS function XWiki.Document#getURL does not take into account the wiki name.
    return fetch('/xwiki/rest/wikis').then(res => res.text())
    .then(data => {
      data = (new DOMParser()).parseFromString(data, "text/xml");
      let wikis = Array.from(data.activeElement.getElementsByTagName('id')).map(a => a.innerHTML);
      let flashNotifierUrl = '/xwiki/__subwiki__/get/Flash/FlashNotifier';
      flashNotifierUrl = flashNotifierUrl.replace('__subwiki__', wikis.length > 1 ? 'wiki/$doc.getWiki()' : 'bin');
      // End workaround.
      return $.ajax({
        url : flashNotifierUrl,
        data : {'ajax' : '1', 'flash' : params['document']}
      }).done(function() {
        // Show the next popup when the current one is closed.
        $(document).one('hidden.bs.modal', '#flashPopup', function(event) {
          window.flashNotifierIndex++;
          window.flashNotifierNext();
        });
      });
    });
  };

  /**
   * Update the modal content when shown.
   */
  $(document).on('show.bs.modal', '#flashPopup', function(event) {
    let params = window.flashNotifierParams[window.flashNotifierIndex];
    $(this).find('.modal-title').text(params['date']);
    $(this).find('.modal-body').html(params['message']);
  }).on('shown.bs.modal', '#flashPopup', function(event) {
    // The popup is ready now.
    $(this).find('input.btn-primary').prop('disabled', false);
  });

  $(document).on('click', '#flashPopup input.btn-primary', function(event) {
    var okButton = $(this).prop('disabled', true);
    // Wait for the confirmation request to succeed and only then hide the popup.
    window.flashNotifierConfirm().then(function() {
      // Hide the current popup and possibly trigger the next popup.
      window.flashPopup.modal('hide');
    }).catch(function() {
      // Re-enable the OK button in case the confirmation request failed.
      okButton.prop('disabled', false);
    });
  });
});
